import React, { useState } from 'react';

const Portfolio = () => {
  const [activeImage, setActiveImage] = useState(null);

  return (
    <main>
      <div className="container">
        {/* Header */}
        <section style={{ textAlign: 'center', marginBottom: '60px' }}>
          <h1 style={{ fontSize: '2.5rem', marginBottom: '12px', color: '#fff' }}>
            Our Work Speaks for Itself
          </h1>
          <p style={{ color: '#ccc', fontSize: '1.1rem' }}>
            A curated collection of digital experiences crafted by <strong>Millennial GFX</strong>.
          </p>
        </section>

        {/* Project Grid */}
        <section style={gridStyle}>
          {projects.map((item, i) => (
            <div
              key={i}
              style={cardStyle}
              onClick={() => setActiveImage(item.image)}
            >
              <div style={imageWrapperStyle}>
                <img src={item.image} alt={item.title} style={imageStyle} />
              </div>
              <div style={contentStyle}>
                <h3 style={titleStyle}>{item.title}</h3>
                <p style={descStyle}>{item.description}</p>
              </div>
            </div>
          ))}
        </section>

        {/* CTA */}
        <section style={{ textAlign: 'center', marginTop: '60px' }}>
          <h2 style={{ color: '#fff', fontSize: '1.8rem', marginBottom: '10px' }}>
            Let’s Build Something Remarkable
          </h2>
          <p style={{ color: '#bbb', marginBottom: '20px' }}>
            Have an idea? Let's transform it into a world-class product.
          </p>
          <a
            href="/contact"
            style={ctaStyle}
          >
            Start Your Project →
          </a>
        </section>

        {/* Image Modal */}
        {activeImage && (
          <div style={modalOverlay} onClick={() => setActiveImage(null)}>
            <div style={modalContent}>
              <img src={activeImage} alt="Full preview" style={modalImage} />
            </div>
          </div>
        )}
      </div>
    </main>
  );
};

// Data
