import React from 'react';

const Services = () => (
  <main>
    <div className="container">
      {/* Page Heading */}
      <h1 style={{ fontSize: '2.4rem', marginBottom: '10px' }}>What We Do Best</h1>
      <p style={{ color: '#ccc', fontSize: '1.05rem' }}>
        At <strong>Millennial GFX</strong>, we bring your vision to life with powerful digital tools. 
        From MVPs to enterprise-scale solutions, we engineer custom systems that move businesses forward.
      </p>

      <hr style={{ border: 'none', borderTop: '1px solid #444', margin: '30px 0' }} />

      {/* Services Grid */}
      <div style={{
        display: 'grid',
        gridTemplateColumns: 'repeat(auto-fit, minmax(280px, 1fr))',
        gap: '30px',
        marginBottom: '50px'
      }}>
        {services.map((service, i) => (
          <div key={i} style={cardStyle}>
            <div style={{ fontSize: '2rem' }}>{service.icon}</div>
            <h3 style={{ color: '#ffd700', margin: '15px 0 10px' }}>{service.title}</h3>
            <p style={{ color: '#ccc' }}>{service.description}</p>
          </div>
        ))}
      </div>

      {/* Our Approach Section */}
      <h2 style={{ fontSize: '1.6rem', marginBottom: '10px' }}>Our Approach</h2>
      <p style={{ color: '#ccc', marginBottom: '10px' }}>
        We don‚Äôt just deliver services‚Äîwe deliver outcomes. Our process is rooted in agile methods,
        transparent communication, and collaborative execution.
      </p>
      <ul style={{ paddingLeft: '20px', marginBottom: '40px', color: '#bbb' }}>
        <li>üîç Discovery & Requirements Gathering</li>
        <li>üé® Wireframing, Design, and Prototyping</li>
        <li>‚öôÔ∏è Agile Development & Iterative Testing</li>
        <li>üöÄ Launch, Support & Continuous Improvement</li>
      </ul>

      {/* CTA */}
      <h2 style={{ fontSize: '1.5rem', marginBottom: '12px' }}>Need a Custom Solution?</h2>
      <p style={{ marginBottom: '20px', color: '#ccc' }}>
        Whether you're building a tech startup, scaling your backend, or reinventing your brand, 
        we‚Äôre ready to collaborate with you.
      </p>
      <a
        href="/contact"
        style={{
          display: 'inline-block',
          backgroundColor: '#ffd700',
          color: '#0a0a36',
          padding: '12px 20px',
          borderRadius: '6px',
          fontWeight: 'bold',
          textDecoration: 'none'
        }}
      >
        Let‚Äôs Build Together ‚Üí
      </a>
    </div>
  </main>
);

const services = [
  {
    icon: 'üíª',
    title: 'Custom Web Development',
    description: 'Robust, scalable websites & web apps tailored for performance and growth.',
  },
  {
    icon: 'üì±',
    title: 'Mobile App Development',
    description: 'iOS and Android apps built for experience, functionality, and scale.',
  },
  {
    icon: 'üé®',
    title: 'UI/UX & Branding',
    description: 'Designs that reflect your identity and create user delight across platforms.',
  },
  {
    icon: '‚òÅÔ∏è',
    title: 'Cloud & DevOps',
    description: 'Modern infrastructure, CI/CD pipelines, and cloud-native scalability.',
  },
  {
    icon: 'üß†',
    title: 'AI & Automation',
    description: 'Integrate intelligence into your business with custom AI workflows.',
  },
  {
    icon: 'üìà',
    title: 'Digital Strategy',
    description: 'Marketing, SEO & analytics to elevate your reach and engagement.',
  }
];

const cardStyle = {
  backgroundColor: '#111141',
  borderRadius: '12px',
  padding: '24px',
  boxShadow: '0 4px 20px rgba(0,0,0,0.3)',
  transition: 'transform 0.3s ease',
};

export default Services;
